import React, { useState } from 'react';
import './login.css';
import { validateOtp } from '../utils/validate'; // Adjust the path if needed

function Login() {
    const [email, setEmail] = useState('');
    const [otp, setOtp] = useState('');
    const [enteredOtp, setEnteredOtp] = useState('');

    const handleGenerateOTP = (e) => {
        e.preventDefault();
        
        fetch('http://localhost:3001/otp', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email }),
        })
            .then(response => response.json())
            .then(data => {
                if (data.otp) {
                    setOtp(data.otp);
                    alert('OTP sent successfully');
                } else {
                    alert('Failed to send OTP');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('Failed to fetch OTP');
            });
    };
    
    const validate = (e) => {
        e.preventDefault();

        if (validateOtp(otp, enteredOtp)) {
            alert('OTP validated');
        } else {
            alert('Incorrect OTP');
        }
    };

    return (
        <div className="login-container">
            <form className="login-form">
                <h2>Login</h2>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button className="login-button" onClick={handleGenerateOTP}>Generate OTP</button>

                <div className="form-group">
                    <label>Enter OTP:</label>
                    <input
                        type="text"
                        value={enteredOtp}
                        onChange={(e) => setEnteredOtp(e.target.value)}
                        required
                    />
                </div>
                <button className="login-button" onClick={validate}>Validate OTP</button>
            </form>
        </div>
    );
}

export default Login;
