import React, { useState } from 'react';
import './login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [cnfmEmail,setCnfmEmail]=useState();
    const handleGenerateOTP = (e) => {
        e.preventDefault(); // Prevent form submission


        fetch('http://localhost:3001/otp', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email }), // Ensure `email` is the correct state variable
        })
            .then(response => response.json())
            .then(data => {
                console.log('OTP sent:', data);
                alert('OTP sent to your email!');
            })
            .catch(error => {
                console.error('Error:', error);
            });
    };
    const validate=()=>{
        if(email===cnfmEmail){
            alert('done');
        }
        else{
            alert('invalid')
        }
    }
    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleGenerateOTP}>
                <h2>Login</h2>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button className="login-button" type="submit">Generate OTP</button>
                <input type='number' onChange={(e)=>setCnfmEmail(e.target.value)}></input>
                <button onClick={validate}>Validate Otp</button>
            </form>
        </div>
    );
}

export default Login;
