import React, { useState } from 'react';
import './login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [otp, setOtp] = useState(''); // Store the OTP received from the server
    const [enteredOtp, setEnteredOtp] = useState(''); // Store the OTP entered by the user
    let array=[];
    // Handle OTP generation
    const handleGenerateOTP = (e) => {
        e.preventDefault();
        
        fetch('http://localhost:3001/otp', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email }), // Send the email as JSON
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log('OTP response:', data);
                if (data.otp) { // Check if OTP is in the response
                    setOtp(data.otp); // Set the OTP received from the server
                    alert('OTP sent to your email!');
                } else {
                    alert('Failed to retrieve OTP');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                alert('Failed to fetch OTP');
            });
    };
    array.push(otp);
    
    // Validate OTP
    const validate = (e) => {
        e.preventDefault(); // Prevent form submission

        if (enteredOtp === otp) {
            alert('OTP validated successfully');
        } else {
            alert('Invalid OTP');
        }
    };

    return (
        <div className="login-container">
            <form className="login-form">
                <h2>Login</h2>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <button className="login-button" onClick={handleGenerateOTP}>Generate OTP</button>

                <div className="form-group">
                    <label>Enter OTP:</label>
                    <input
                        type="text"
                        value={enteredOtp}
                        onChange={(e) => setEnteredOtp(e.target.value)}
                        required
                    />
                </div>
                <button className="login-button" onClick={validate}>Validate OTP</button>
            </form>
        </div>
    );
}

export default Login;
