import React, { useState } from 'react';
import './login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [otp, setOtp] = useState(''); // Store the OTP received from the server
    const [enteredOtp, setEnteredOtp] = useState(''); // Store the OTP entered by the user

    // Handle OTP generation
    const handleGenerateOTP = (e) => {
        e.preventDefault();

        fetch('http://localhost:3001/otp', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email }), // Send the email as JSON
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network err da sunni');
                }
                return response.json();
            })
            .then((data) => {
                console.log('OTP response:', data);
                if (data.otp) { // Check if OTP is in the response
                    setOtp(data.otp); // Set the OTP received from the server
                    alert('annupite da punda');
                } else {
                    alert('otp pogala da thayoli');
                }
            })
            .catch((error) => {
                console.error('Error:', error);
                alert('Failed to fetch OTP');
            });
    };

    // Validate OTP
    const validate = (e) => {
        e.preventDefault(); // Prevent form submission
        console.log(enteredOtp);
        console.log('otp',otp);
        if (enteredOtp === otp) {
            alert('Gotha perea aalu punda than Otp validated');
        } else {
            alert('Paathu kooda olungu pundaya poda terlaya da echa thayoli');
        }
    };

    return (
        <div className="login-container">
            <form className="login-form">
                <h2>Login</h2>

                {/* Email Input */}
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Enter your email"
                    />
                </div>

                {/* Generate OTP Button */}
                <button className="login-button" onClick={handleGenerateOTP}>
                    Generate OTP
                </button>

                {/* OTP Input */}
                <div className="form-group">
                    <label>Enter OTP:</label>
                    <input
                        type="text"
                        value={enteredOtp}
                        onChange={(e) => setEnteredOtp(e.target.value)}
                        required
                        placeholder="Enter the OTP"
                    />
                </div>

                {/* Validate OTP Button */}
                <button className="login-button" onClick={validate}>
                    Validate OTP
                </button>
            </form>
        </div>
    );
}

export default Login;
